import React, { Component } from "react";
import "./style.scss";
import axios from "axios";



function dateFormat(timestamp) {
  const dateObject = new Date(timestamp);
  const date =
    dateObject.getMonth() +
    1 +
    "/" +
    dateObject.getDate() +
    "/" +
    dateObject.getFullYear();
  return date;
};

function formatDate(date) {
  return date.toLocaleDateString();
}
