import React, { Component } from "react";
import Hero from "../Hero/Hero";
import Main from "../Main/Main";
import axios from "axios";

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mainVideo:[]
          };
  }
  componentDidMount() {
    axios
      .get(
        "https://project-2-api.herokuapp.com/videos/1af0jruup5gu?api_key=851fad5d-3ca8-4837-aed1-2384b3fba526"
      )
      .then((res) => {
        this.setState({ mainVideo: res.data });
      }).catch((error)=>{
        console.log('mainVideo Axios error')
      })
  }
  render() {
    return (
      <div>
        <Hero video={this.state.mainVideo.video} image={this.state.mainVideo.image} />
        {/* <Main description={this.state.video.description} comment={this.state.video} videoList={this.state.video}/> */}
        <Main />
      </div>
    );
  }
}
