import React, { Component } from "react";
import VideoCard from "../VideoCard/VideoCard";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams,
  useRouteMatch,
} from "react-router-dom";
import "./style.scss";

export default function PlaylistSection(props) {
  let videoToRender;
  let { path, url } = useRouteMatch();

  if (props.sideVideo) {
    videoToRender = props.sideVideo.map((item) => {
      return (
        <Link key={item.id} to={`${url}/videos/${item.id}`}>
          <VideoCard
            src={item.image}
            channel={item.channel}
            title={item.title}
            // key={item.id}
          />
        </Link>
      );
    });
  }
  return (
    <section className="playlistSection playlist">
      <h3 className="playlist__title">NEXT VIDEO</h3>

      <section className="playlist__cards playlistCards">
        {videoToRender}
      </section>
    </section>
  );
}
